package explorationday1;

public class ClassLearningArthmeticOperators1{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char character1=120; 
		char character2='@'; 
		int number1=500;    
		int number2=980 ;
		int Sumchar=character1+character2;
		int Subint=number1-number2;
		int Mutint=character1*character2;
		int Modint=number1%number2;
		int Divint=character1/character2;
		number2++;
		number1--;
	System.out.println("Addition of two characters is "+Sumchar);
	System.out.println("Subtraction of two numbers is "+Subint);
	System.out.println("Multiplication of two characters  is "+Mutint);
	System.out.println("Modulus of two numbers is "+Modint);
	System.out.println("Division of two numbers is "+Divint);
	System.out.println("Increment of number 2 is "+number2);
	System.out.println("Decrement of number 1 is "+number1);
					
	}
}
