package explorationday1;

public class ClassLearningPrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Maxnumber=2147483647;
		int Minnumber=-2147483648;
		boolean value1=false;
		boolean value2=true;
		char Maxchar='0';
		char Minchar='\u0001';
		byte Minbit=-128;
		byte Maxbit=127;
		short Maxshort=-32768;
		short Minshort=32767;
		long Maxlong=9223372036854775807L;
		long Minlong=-9223372036854775808L;
		double Maxdouble=+1.79769313486231570E+308D;
		double Mindouble=-1.79769313486231570E+308D;
		float Minfloat=-340282347f;
		float Maxfloat=+340282347F;
		System.out.println("Integer data type Max Value " +Maxnumber);	
		System.out.println("Integer data type Max Value " +Minnumber);	
		System.out.println("Double data type Max Value "+Maxdouble);
		System.out.println("Double data type Max Value "+Mindouble);
		System.out.println("Float data type Max Value "+Minfloat);
		System.out.println("Float data type Max Value "+Maxfloat);
		System.out.println("Long data type Max Value "+Maxlong);
		System.out.println("Long data type Max Value "+Minlong);
		System.out.println("Short data type Max Value "+Maxshort);
		System.out.println("Short data type Max Value "+Minshort);
		System.out.println("Boolean data type Max Value " +value1);
		System.out.println("Boolean data type Max Value " +value2);
		System.out.println("Byte data type Max Value "+Maxbit);
		System.out.println("Byte data type Min Value "+Minbit);
		System.out.println("Character data type Max Value "+Maxchar);
		System.out.println("Character data type Max Value "+Minchar);
			
				
	}

}
